INSERT INTO `am_links` VALUES (1,'自留地.文字FM','http://www.9yifa.com/','美文','一块属于文字的自留地，听听歌写写字','','','','1474954567',0,50),(2,'方室网志','http://fangshi.org','生活','方室网志即方氏博客，系个人生活博客，记录着生活琐事、胡思乱想、闲言碎语，看官不必太过在意。因方氏过于直白，而室兼有房间和家族之意，颇有意味，故改称方室。','','','','1474954553',0,61),(3,'无声有处','http://www.hannahwu.com/','苏州，生活，旅游','一个褪去燥热的夏夜，开始记录苏州生活的点滴，喜爱吴侬软语，小桥流水。漂泊的风，浅浅的月光，苏州的味道。摄影摇曳生姿，笔墨缕缕飘香，忽然，爱上这般诗意生活。','','','','1474954537',0,61),(4,'Mr.He','http://mrhe.net/','生活，旅游','一个记录生活与旅游的个人博客','','','','1474954511',0,54),(5,'常阳时光','https://cyhour.com/','生活','常阳时光,嫦子,老杨,WordPress,个人网站,独立博客,平凡生活,健康快乐,广东德庆','','','','1474954497',0,66),(16,'风雪之隅 ','http://www.laruence.com/','php','PHP语言, PHP扩展, Zend引擎相关的研究,技术,新闻分享 – 左手代码 右手诗 ','','','','1475938200',0,43),(18,'柳betty','http://forbetty.com/','','','','','','1476459968',0,55),(19,'从良未遂','http://www.ithmz.com/','','','','','','1476460346',0,41),(20,'叶金荣','http://imysql.cn/','MySQL','专注MySQL十余年，最靠谱的知数堂MySQL培训联合创始人','叶金荣','','','1476521405',0,49),(21,'董的博客','http://dongxicheng.org','大规模数据处理，Hadoop, HDFS, YARN, MapReduce，Spark，Scribe，Mesos等','董的博客|研究点滴','董西成','','','1477274363',0,42),(22,'octopress','http://octopress.org','octopress','A blogging framework for hackers.','','','','1477550383',0,31),(23,'安卓开发指南','http://dev.apkbus.com/','安卓','安卓开发指南','','','','1478229256',0,37),(24,'UML建模工具','http://itlu.org/zhidao/q-1.html','UML建模工具','','','','','1478519140',0,31),(25,'David A.W. Barton ','http://www.cityinthesky.co.uk/','','David A.W. Barton ― City in the Sky','','','','1479177381',0,27),(26,'linux运维笔记','https://blog.linuxeye.com/','linux','','','','','1480680214',0,16),(27,'李阳博客','https://www.liyang.io','hadoop','','','','','1480680377',0,16),(28,'设计笔记','https://biji.io','css','','','','','1480680490',0,17),(29,'swoole官方网站','http://www.swoole.com/','swoole，php','Swoole：重新定义PHP','','','','1481427340',0,17),(30,'方糖气球','http://ftqq.com','LazyPHP','','','','','1483454187',0,0);
